import { animate, style, transition, trigger } from '@angular/animations';
import { Input } from '@angular/core';
ansition, trstyle, animate, from '@angular/core';
import { Server } from 'src/app/models/server';

@Component({
  selector: 'app-server-options-button',
  templateUrl: './server-options-button.component.html',
  styleUrls: ['./server-options-button.component.css'],
  animations: [
      trigger(
        'inOutAnimation', 
        [
          transition(
            ':enter', 
            [
              style({ height: 0, opacity: 0 }),
              animate('1s ease-out', 
                     style({ height: 300, opacity: 1 }))
            ]
          ),
          transition(
            ':leave', 
            [
              style({ height: 300, opacity: 1 }),
              animate('1s ease-in', 
                     style({ height: 0, opacity: 0 }))
            ]
          )
        ]
      )
    ]
})
export class ServerOptionsButtonComponent {
  @Input() server?: Server;
  showOptions = false;
  showInviteCode = false;

  toggle() {
    this.showOptions = !this.showOptions;
  }

}
