import { Component, OnInit } from '@angular/core';
import { ServersService } from '../servers.service';
import { ServerSimple } from '../models/server';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-servers',
  templateUrl: './servers.component.html',
  styleUrls: ['./servers.component.css']
})
export class ServersComponent implements OnInit {
  servers: ServerSimple[] = [];
  currentServer?: ServerSimple;
  showCreateServerModal = false;

  constructor(
    private serversService: ServersService, 
    private router: Router,
    public route: ActivatedRoute
  ) {}

  ngOnInit(): void {
    this.serversService.getServers().subscribe((data) => {
      this.servers = data;
      // Redirect to the first server if the user has any.
      if (this.servers.length > 0) {
        this.router.navigate(["servers/", this.servers[0].id]);
      }
      else {
        this.showCreateServerModal = true;
      }
    })

    this.route.paramMap.subscribe(params => {
      let id = params.get('id');
    });
  }
}
