import { ChangeDetectionStrategy, ChangeDetectorRef, Component, ElementRef, EventEmitter, Input, OnChanges, OnInit, Output, SimpleChanges, ViewChild, inject } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-dialog',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './dialog.component.html',
})
export class DialogComponent implements OnChanges{
  @Input() open = false;
  @ViewChild('dialog') dialog: ElementRef<HTMLDialogElement> | undefined;

  ngOnChanges(changes: SimpleChanges): void {
    if (changes.dialog) {
      if (this.open) {
        this.dialog?.nativeElement.showModal();
        console.log("open");
      }
      else {
        this.dialog?.nativeElement.close();
        console.log("close");
      }
    }
  }
}
